mapper.select:
SELECT
TH.EXECUTE_ID,TH.TASK_ID,TH.SHOULD_FIRE_TIME,TH.ACTUAL_FIRE_TIME,TH.FIRE_TIME,TH.EXECUTE_RESULT,TH.COMPLETE_TIME,T.TASK_NAME,T.CLASS_NAME,T.OWNER,TH.TRIGGER_TYPE
FROM DDC_TASK_EXECUTE_HISTORY AS TH JOIN DDC_TASK AS T ON TH.TASK_ID=T.TASK_ID


        AND T.TASK_NAME LIKE CONCAT(CONCAT('%',#{job.taskName,jdbcType=VARCHAR},'%'))


        AND T.CLASS_NAME = #{job.contentType,jdbcType=VARCHAR}


        AND T.OWNER= #{job.assignee,jdbcType=VARCHAR}


        AND TH.SHOULD_FIRE_TIME >= #{job.loadDateFrom,jdbcType=BIGINT,typeHandler=com.iwellmass.dispatcher.admin.MillsHandler}


        AND TH.SHOULD_FIRE_TIME <= #{job.loadDateTo,jdbcType=BIGINT,typeHandler=com.iwellmass.dispatcher.admin.MillsHandler}


        AND TH.ACTUAL_FIRE_TIME >= #{job.excuteTimeFrom,jdbcType=TIMESTAMP}


        AND TH.ACTUAL_FIRE_TIME <= #{job.excuteTimeTo,jdbcType=TIMESTAMP}


        AND TH.TRIGGER_TYPE= #{job.type,jdbcType=INTEGER}


ORDER BY EXECUTE_ID DESC
LIMIT #{pager.page},#{pager.limit}

 SELECT
 TH.EXECUTE_ID,TH.TASK_ID,TH.SHOULD_FIRE_TIME,TH.ACTUAL_FIRE_TIME,TH.FIRE_TIME,TH.COMPLETE_TIME,TH.EXECUTE_RESULT,TH.EXECUTE_RESULT,T.TASK_NAME,T.CLASS_NAME,T.OWNER,TH.TRIGGER_TYPE
 FROM DDC_TASK_EXECUTE_HISTORY AS TH JOIN DDC_TASK AS T ON TH.TASK_ID=T.TASK_ID


         AND T.TASK_NAME LIKE CONCAT(CONCAT('%',#{taskName,jdbcType=VARCHAR},'%'))


         AND T.CLASS_NAME = #{contentType,jdbcType=VARCHAR}


         AND T.OWNER= #{assignee,jdbcType=VARCHAR}


         AND TH.SHOULD_FIRE_TIME >= #{loadDateFrom,jdbcType=BIGINT,typeHandler=com.iwellmass.dispatcher.admin.MillsHandler}


         AND TH.SHOULD_FIRE_TIME <= #{loadDateTo,jdbcType=BIGINT,typeHandler=com.iwellmass.dispatcher.admin.MillsHandler}


         AND TH.ACTUAL_FIRE_TIME >= #{excuteTimeFrom,jdbcType=TIMESTAMP}


         AND TH.ACTUAL_FIRE_TIME <= #{excuteTimeTo,jdbcType=TIMESTAMP}


         AND TH.TRIGGER_TYPE= #{type,jdbcType=INTEGER}


ORDER BY EXECUTE_ID DESC
